<?phprequire_once("init.php");global $db;header('Content-type: application/json');if(!isset($_REQUEST["email"]) || !isset($_REQUEST["pass"]) || !isset($_REQUEST["passRp"])){	echo '{"success":"false", error":"invalid request"}';	return;}$email = $_REQUEST["email"];$passwd = $_REQUEST["pass"];$passwdRp = $_REQUEST["passRp"];if($passwd === $passwdRp){		$result = mysqli_query($db,"SELECT * FROM user");	$exists = false;	while($row = mysqli_fetch_array($result)){	  $exists = true;	}	if($exists){		echo '{"success":"false", "error": "exists"}';		} else {		if(!mysqli_query($db,"INSERT INTO user VALUES ('".$email."', md5('".$passwd."'))")){			echo '{"success":"false", error":"db error: '.mysqli_error($db).'"}';		} else {			echo '{"success":"true", error":"user added"}';		}		}	  	} else {	echo '{"success":"false", error":"passwd dont match"}';}require_once("dist.php");?>