<?phprequire_once("init.php");global $db;header('Content-type: application/json');if(!isset($_REQUEST["email"]) || !isset($_REQUEST["pass"]) || !isset($_REQUEST["passRp"])){	echo '{"success":"false", error":"invalid request"}';	return;}$email = $_REQUEST["email"];$passwd = $_REQUEST["pass"];$passwdRp = $_REQUEST["passRp"];if($passwd === $passwdRp){		$query = sprintf("SELECT * FROM user WHERE email = '%s'", @mysql_real_escape_string($email));	$result = mysqli_query($db,$query);	$exists = false;	while($row = mysqli_fetch_array($result)){	  $exists = true;	}	if($exists){		echo '{"success":"false", "error": "exists"}';		} else {		$query = sprintf("INSERT INTO user VALUES ('%s', md5('%s'))", @mysql_real_escape_string($email), @mysql_real_escape_string($passwd));		if(!mysqli_query($db,$query)){			echo '{"success":"false", "error":"db error: '.mysqli_error($db).'"}';		} else {			echo '{"success":"true", "error":"user added"}';		}		}	  	} else {	echo '{"success":"false", error":"passwd dont match"}';}require_once("dist.php");?>